mapper.update:
update topic set
usersid=#{usersid} , courseid=#{courseid} , num=#{num} ,
contents=#{contents} , addtime=#{addtime} where topicid=#{topicid}

mapper.select:
select a.* , b.username , c.coursename from topic a , users b , course
c where a.usersid = b.usersid and a.courseid = c.courseid

	and a.usersid = #{usersid}


	and a.courseid = #{courseid}


	and a.num = #{num}


	and a.contents = #{contents}


	and a.addtime = #{addtime}
